<script lang="ts">
  import { goto } from '$app/navigation';
	import { AppwriteService } from '$lib/AppwriteService';
  import { authStore } from '$lib/stores/authStore';

	let name = '';
	let email = '';
	let password = '';
	let passwordAgain = '';

	let error = '';

	async function onSubmit() {
		if (password !== passwordAgain) {
			error = 'Passwords do not match.';
			return;
		}

		try {
			await AppwriteService.register(name, email, password);
			await AppwriteService.login(email, password);
			$authStore = await AppwriteService.getAccount();
			goto('/');
		} catch (err: any) {
			error = err.message;
		}
	}
</script>

<img
	src="/img/techscrunch-logo.svg"
	class="u-block u-margin-inline-auto"
	alt="Tech Scrunch"
	width="70"
	height="43"
/>

<p class="u-margin-block-start-16 u-text-center">Hi, there!</p>
<h1 class="heading-level-2 u-margin-block-start-24 u-text-center">Sign in</h1>

<form class="form u-margin-block-start-16" on:submit|preventDefault={onSubmit}>
	<ul class="form-list">
		<li class="form-item">
			<input
				required={true}
				bind:value={name}
				class="input-text"
				type="text"
				placeholder="Full Name"
				aria-label="Full name"
			/>
		</li>
		<li class="form-item">
			<input
				required={true}
				bind:value={email}
				class="input-text"
				type="email"
				placeholder="Email address"
				aria-label="Email address"
			/>
		</li>
		<li class="form-item">
			<input
				required={true}
				bind:value={password}
				class="input-text"
				type="password"
				placeholder="Password"
				aria-label="Password"
			/>
		</li>
		<li class="form-item">
			<input
				required={true}
				bind:value={passwordAgain}
				class="input-text"
				type="password"
				placeholder="Password Again"
				aria-label="Repeat Password"
			/>
		</li>
	</ul>

	<p class="u-margin-block-start-16 u-text-center">
		By clicking "Next", you agree to the Terms and acknowledge the Privacy Policy to create a
		TechScrunch account
	</p>

	<button type="submit" class="button u-margin-block-start-24 u-width-full-line">
		<span class="text">Next</span>
	</button>

	{#if error}
		<p class="u-margin-block-start-12 u-text-center u-color-text-danger">{error}</p>
	{/if}

	<div class="u-margin-block-start-24">
		<h2 class="u-text-center"><a href="/auth/login" class="link">Sign in</a> or, continue with</h2>
		<div class="u-flex u-gap-12 u-margin-block-start-12">
			<button
				type="button"
				on:click={AppwriteService.oauthLogin}
				class="button-reverse u-stretch"
				aria-label="Login with Github"
				style="background-image:url(data:image/png;base64,);"
			/>
			<!-- <button
				class="button-reverse u-stretch"
				aria-label="Login with Google"
				style="background-image: url(data:image/svg+xml;base64,);"
			/> -->
		</div>
	</div>
</form>
